import { Component, OnInit } from '@angular/core';
import { LoginService } from '../../services/login-service.service';
import { User } from '../../interfaces/user';
import { ActivatedRoute, Router, NavigationExtras } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  user: User;

  constructor(
    private loginService: LoginService,
    public route: ActivatedRoute,
    public location: Location,
    public router: Router,
  ) {}

  ngOnInit(): void {
    this.loginService.getUser();
  }
  send(): void {}
  clear(): void {}
}
<div class="container border rounded d-flex  justify-content-center">
    <form class="d-flex" #loginForm="ngForm" (ngSubmit)="send()">
        <div class="d-flex mb-3 form-group">
            <input 
                type="text" 
                placeholder="Username" 
                required 
                class="form-control border border-dark">
            <input 
                type="password"
                placeholder="Password" 
                required class="border border-dark form-control"> 
        </div>
        <div>
            <button type="submit" (click)="send()" class="btn btn-primary btn-sm col-6 m-2 border border-dark">Sign in</button>
        </div> 
    </form>
</div>
